import React from 'react';
import { Link, Element } from 'react-scroll';
import Navebar from './Navebar'; // Corrected spelling

function About() {
    return (
        <>
        <Navebar />
        <nav className="about-nav">
            <ul className="about-nav-list">
                <li>
                    <Link
                        to="section1"
                        smooth={true}
                        duration={500}
                        className="about-nav-link"
                        activeClass="active-link"
                        spy={true}
                        offset={-80}
                    >
                        Section 1
                    </Link>
                </li>
                <li>
                    <Link
                        to="section2"
                        smooth={true}
                        duration={500}
                        className="about-nav-link"
                        activeClass="active-link"
                        spy={true}
                        offset={-80}
                    >
                        Section 2
                    </Link>
                </li>
            </ul>
        </nav>
        <div style={{ fontFamily: 'Segoe UI, Arial, sans-serif', background: '#f4f6fb', minHeight: '100vh', paddingTop: '100px' }}>
            <Element name="section1">
                <section style={{
                    minHeight: '100vh',
                    background: 'linear-gradient(135deg, #e3f0ff 0%, #b6e0fe 100%)',
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',
                    justifyContent: 'center',
                    borderRadius: '16px',
                    margin: '2rem auto',
                    maxWidth: '800px',
                    boxShadow: '0 4px 24px rgba(25, 118, 210, 0.08)',
                }}>
                    <h1 style={{ color: '#1976d2', fontSize: '2.5rem', marginBottom: '1rem' }}>Section 1</h1>
                    <p style={{ fontSize: '1.2rem', color: '#333', maxWidth: '600px', textAlign: 'center' }}>
                        This is the content of section 1
                    </p>
                </section>
            </Element>
            <Element name="section2">
                <section style={{
                    minHeight: '100vh',
                    background: 'linear-gradient(135deg, #e0ffe3 0%, #b6febf 100%)',
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',
                    justifyContent: 'center',
                    borderRadius: '16px',
                    margin: '2rem auto',
                    maxWidth: '800px',
                    boxShadow: '0 4px 24px rgba(25, 210, 118, 0.08)',
                }}>
                    <h1 style={{ color: '#388e3c', fontSize: '2.5rem', marginBottom: '1rem' }}>Section 2</h1>
                    <p style={{ fontSize: '1.2rem', color: '#333', maxWidth: '600px', textAlign: 'center' }}>
                        This is the content of section 2
                    </p>
                </section>
            </Element>
        </div>
        <style>
            {`
                .about-nav {
                    position: fixed;
                    top: 0;
                    width: 100%;
                    background: #fff;
                    box-shadow: 0 2px 8px rgba(0,0,0,0.07);
                    z-index: 100;
                }
                .about-nav-list {
                    display: flex;
                    justify-content: center;
                    list-style: none;
                    margin: 0;
                    padding: 1rem 0;
                    gap: 2rem;
                }
                .about-nav-link {
                    color: #1976d2;
                    text-decoration: none;
                    font-weight: 600;
                    font-size: 1.1rem;
                    cursor: pointer;
                    transition: color 0.2s;
                }
                .about-nav-link:hover,
                .about-nav-link.active-link {
                    color: #388e3c !important;
                    border-bottom: 2px solid #388e3c;
                    padding-bottom: 2px;
                }
            `}
        </style>
        </>
    );
}

export default About;
