import React from 'react';
import { Link, Element } from 'react-scroll';

function About() {
    return (
        <div style={{ fontFamily: 'Segoe UI, Arial, sans-serif', background: '#f4f6fb', minHeight: '100vh' }}>
            <nav style={{
                position: 'fixed',
                top: 0,
                width: '100%',
                background: '#fff',
                boxShadow: '0 2px 8px rgba(0,0,0,0.07)',
                zIndex: 100,
            }}>
                <ul style={{
                    display: 'flex',
                    justifyContent: 'center',
                    listStyle: 'none',
                    margin: 0,
                    padding: '1rem 0',
                    gap: '2rem',
                }}>
                    <li>
                        <Link
                            to="section1"
                            smooth={true}
                            duration={500}
                            style={{
                                color: '#1976d2',
                                textDecoration: 'none',
                                fontWeight: 600,
                                fontSize: '1.1rem',
                                cursor: 'pointer',
                                transition: 'color 0.2s',
                            }}
                            activeClass="active-link"
                        >
                            Section 1
                        </Link>
                    </li>
                    <li>
                        <Link
                            to="section2"
                            smooth={true}
                            duration={500}
                            style={{
                                color: '#1976d2',
                                textDecoration: 'none',
                                fontWeight: 600,
                                fontSize: '1.1rem',
                                cursor: 'pointer',
                                transition: 'color 0.2s',
                            }}
                            activeClass="active-link"
                        >
                            Section 2
                        </Link>
                    </li>
                </ul>
            </nav>
            <div style={{ paddingTop: '80px' }}>
                <Element name="section1">
                    <section style={{
                        minHeight: '100vh',
                        background: 'linear-gradient(135deg, #e3f0ff 0%, #b6e0fe 100%)',
                        display: 'flex',
                        flexDirection: 'column',
                        alignItems: 'center',
                        justifyContent: 'center',
                        borderRadius: '16px',
                        margin: '2rem auto',
                        maxWidth: '800px',
                        boxShadow: '0 4px 24px rgba(25, 118, 210, 0.08)',
                    }}>
                        <h1 style={{ color: '#1976d2', fontSize: '2.5rem', marginBottom: '1rem' }}>Section 1</h1>
                        <p style={{ fontSize: '1.2rem', color: '#333', maxWidth: '600px', textAlign: 'center' }}>
                            This is the content of section 1
                        </p>
                    </section>
                </Element>
                <Element name="section2">
                    <section style={{
                        minHeight: '100vh',
                        background: 'linear-gradient(135deg, #e0ffe3 0%, #b6febf 100%)',
                        display: 'flex',
                        flexDirection: 'column',
                        alignItems: 'center',
                        justifyContent: 'center',
                        borderRadius: '16px',
                        margin: '2rem auto',
                        maxWidth: '800px',
                        boxShadow: '0 4px 24px rgba(25, 210, 118, 0.08)',
                    }}>
                        <h1 style={{ color: '#388e3c', fontSize: '2.5rem', marginBottom: '1rem' }}>Section 2</h1>
                        <p style={{ fontSize: '1.2rem', color: '#333', maxWidth: '600px', textAlign: 'center' }}>
                            This is the content of section 2
                        </p>
                    </section>
                </Element>
            </div>
            <style>
                {`
                    .active-link {
                        color: #388e3c !important;
                        border-bottom: 2px solid #388e3c;
                        padding-bottom: 2px;
                    }
                    nav ul li:hover a {
                        color: #388e3c;
                    }
                `}
            </style>
        </div>
    );
}

export default About;
